Perform one conversion on Board_ADCCHANNEL_A1 in ADCBuf_RETURN_MODE_BLOCKING. #include<ti/drivers/ADCBuf.h>

#defineADCBUFFERSIZE100

ADCBuf_HandleadcBufHandle;
ADCBuf_ParamsadcBufParams;
ADCBuf_ConversionblockingConversion;
uint16_tsampleBufferOne[ADCBUFFERSIZE];

ADCBuf_Params_init(&adcBufParams);
adcBufHandle=ADCBuf_open(Board_ADCBuf0,&adcBufParams);
if(adcBufHandle==NULL){
//handleerror
}

blockingConversion.arg=NULL;
blockingConversion.adcChannel=Board_ADCCHANNEL_A1;
blockingConversion.sampleBuffer=sampleBufferOne;
blockingConversion.sampleBufferTwo=NULL;
blockingConversion.samplesRequestedCount=ADCBUFFERSIZE;

if(ADCBuf_convert(adcBufHandle,&blockingConversion,1)!=ADCBuf_STATUS_SUCCESS){
//handleerror
}

This specific configuration performs one conversion on Board_ADCCHANNEL_A1 in ADCBuf_RETURN_MODE_BLOCKING. The custom parameters used here are identical to the defaults parameters. Users can of course define their own parameters. #include<ti/drivers/ADCBuf.h>

#defineADCBUFFERSIZE100

ADCBuf_HandleadcBufHandle;
ADCBuf_ParamsadcBufParams;
ADCBuf_ConversionblockingConversion;
uint16_tsampleBufferOne[ADCBUFFERSIZE];
ADCBufCC26X2_ParamsExtensioncustomParams;

ADCBuf_Params_init(&adcBufParams);
customParams.samplingDuration=ADCBufCC26X2_SAMPLING_DURATION_2P7_US;
customParams.refSource=ADCBufCC26X2_FIXED_REFERENCE;
customParams.samplingMode=ADCBufCC26X2_SAMPING_MODE_SYNCHRONOUS;
customParams.inputScalingEnabled=true;

adcBufParams.custom=&customParams;

adcBufHandle=ADCBuf_open(Board_ADCBuf0,&adcBufParams);
if(adcBufHandle==NULL){
//handleerror
}

blockingConversion.arg=NULL;
blockingConversion.adcChannel=Board_ADCCHANNEL_A1;
blockingConversion.sampleBuffer=sampleBufferOne;
blockingConversion.sampleBufferTwo=NULL;
blockingConversion.samplesRequestedCount=ADCBUFFERSIZE;

if(ADCBuf_convert(adcBufHandle,&blockingConversion,1)!=ADCBuf_STATUS_SUCCESS){
//handleerror
}
