detaileddescription.para:
The SPI header file should be included in an application as follows: #include<ti/drivers/SPI.h>
#include<ti/drivers/spi/SPICC26XXDMA.h>
#include<ti/drivers/dma/UDMACC26XX.h>

Receive 100 bytes over SPI in SPI_MODE_BLOCKING. SPI_Handlehandle;
SPI_Paramsparams;
SPI_Transactiontransaction;
uint8_trxBuf[100];//Receivebuffer

//InitSPIandspecifynon-defaultparameters
SPI_Params_init(&params);
params.bitRate=1000000;
params.frameFormat=SPI_POL1_PHA1;
params.mode=SPI_SLAVE;

//Configurethetransaction
transaction.count=100;
transaction.txBuf=NULL;
transaction.rxBuf=rxBuf;

//OpentheSPIandperformthetransfer
handle=SPI_open(Board_SPI,&params);
SPI_transfer(handle,&transaction);

This use case will perform a transfer in SPI_MODE_BLOCKING until the wanted amount of bytes is transferred or until chip select is deasserted by the SPI master. This SPI_transfer() call can be used when unknown amount of bytes shall be transferred. Note: The partial return is also possible in SPI_MODE_CALLBACK mode. SPI_Handlehandle;
SPI_Paramsparams;
SPI_Transactiontransaction;
uint8_trxBuf[100];//Receivebuffer

//InitSPIandspecifynon-defaultparameters
SPI_Params_init(&params);
params.bitRate=1000000;
params.frameFormat=SPI_POL1_PHA1;
params.mode=SPI_SLAVE;

//Configurethetransaction
transaction.count=100;
transaction.txBuf=NULL;
transaction.rxBuf=rxBuf;

//OpentheSPIandinitiatethepartialread
handle=SPI_open(Board_SPI,&params);

//EnableRETURN_PARTIAL
SPI_control(handle,SPICC26XXDMA_RETURN_PARTIAL_ENABLE,NULL);

//Begintransfer
SPI_transfer(handle,&transaction);

This use case will configure the SPI driver to transfer continuously in SPI_MODE_CALLBACK, 16 bytes at the time and echoing received data after every 16 bytes. //Callbackfunction
staticvoidtransferCallback(SPI_Handlehandle,SPI_Transaction*transaction)
{
//Startanothertransfer
SPI_transfer(handle,transaction);
}

staticvoidtaskFxn(uintptr_ta0,uintptr_ta1)
{
SPI_Handlehandle;
SPI_Paramsparams;
SPI_Transactiontransaction;
uint8_tbuf[16];//Receiveandtransmitbuffer

//InitSPIandspecifynon-defaultparameters
SPI_Params_init(&params);
params.bitRate=1000000;
params.frameFormat=SPI_POL1_PHA1;
params.mode=SPI_SLAVE;
params.transferMode=SPI_MODE_CALLBACK;
params.transferCallbackFxn=transferCallback;

//Configurethetransaction
transaction.count=16;
transaction.txBuf=buf;
transaction.rxBuf=buf;

//OpentheSPIandinitiatethefirsttransfer
handle=SPI_open(Board_SPI,&params);
SPI_transfer(handle,&transaction);

//Waitforever
while(true);
}

This use case will configure a SPI master to send the data in txBuf while receiving data to rxBuf in BLOCKING_MODE. SPI_Handlehandle;
SPI_Paramsparams;
SPI_Transactiontransaction;
uint8_ttxBuf[]="HelloWorld";//Transmitbuffer
uint8_trxBuf[11];//Receivebuffer

//InitSPIandspecifynon-defaultparameters
SPI_Params_init(&params);
params.bitRate=1000000;
params.frameFormat=SPI_POL1_PHA1;
params.mode=SPI_MASTER;

//Configurethetransaction
transaction.count=sizeof(txBuf);
transaction.txBuf=txBuf;
transaction.rxBuf=rxBuf;

//OpentheSPIandperformthetransfer
handle=SPI_open(Board_SPI,&params);
SPI_transfer(handle,&transaction);
