staticvoidtaskFxn(uintptr_ta0,uintptr_ta1)
{
UART_Handlehandle;
UART_Paramsparams;

//InitUART
UART_init();

//Specifynon-defaultparameters
UART_Params_init(&params);
params.baudRate=9600;
params.writeMode=UART_MODE_CALLBACK;
params.writeDataMode=UART_DATA_BINARY;
params.writeCallback=writeCallback;
params.readMode=UART_MODE_CALLBACK;
params.readDataMode=UART_DATA_BINARY;
params.readCallback=readCallback;

//OpentheUARTandinitiatethefirstread
handle=UART_open(Board_UART,&params);
wantedRxBytes=16;
intrxBytes=UART_read(handle,rxBuf,wantedRxBytes);

while(true);//Waitforever
}
